import React, {useState} from 'react'

import Toolbar from './Toolbar/Toolbar'
import Sidebar  from './SideBar/Sidebar'
import Backdrop  from './Backdrop/Backdrop'

function Layout() {

    const [showSide, setOpenSide] = useState(false)

   const displaySide=()=>{
        setOpenSide(prevState=>{
        return { showSide : !prevState.showSide}
        })
    }

    const closeSide=()=>{
        setOpenSide(prevState=>{
        return { showSide : !prevState.showSide}
        })
    }

    const openSide = showSide && <Sidebar />
    const showBackdrop = showSide && <Backdrop close={closeSide} />

    return (
        <div>
            <Toolbar openSide={displaySide} />
            {openSide}
            {showBackdrop}

        </div>
    )
}

export default Layout
