import React, {useState} from 'react'

import Toolbar from './Toolbar/Toolbar'
import Sidebar  from './SideBar/Sidebar'
import Backdrop  from './Backdrop/Backdrop'

function Layout() {

    const [showSide, setOpenSide] = useState(false)

   const displaySide=()=>{
        setOpenSide(prevState=>{
        return { showSide : !prevState.showSide}
        })
    }

    const closeSide=()=>{
        setOpenSide({showSide: false})
    }
    console.log(showSide)

    let openSide = null
    let showBackdrop = null

    if(showSide){
        openSide = <Sidebar />
        showBackdrop = <Backdrop close={closeSide} />
    }

    return (
        <div>
            <Toolbar openSide={displaySide} />
            {openSide}
            {showBackdrop}

        </div>
    )
}

export default Layout
